import {
  ApolloClient,
  InMemoryCache,
  gql
} from "@apollo/client";

const client = new ApolloClient({
  uri: ' https://rickandmortyapi.com/graphql ',
  cache: new InMemoryCache()
});

const CHARACTERS_RICKMORTY = gql`
query {
  characters(page: 2, filter: { name: "rick" }) {
    info {
      count
      next
      prev
    }
    results {
      id
      name
      status
      species
      type
      gender
      image
      episode {
        id
        name
        air_date
        episode
        characters
        created
      }
      created
    }
  }
}
`;

export { client };
export { CHARACTERS_RICKMORTY };
